/**
 * API Calls to backend servers
 */

/**
 * Login API Calls
 */

// require("dotenv").config();
const AUTH_BASE_URL =
  process.env.AUTH_BASE_URL || `http://localhost:8000/api/auth`;

export const loginUser = async (body) => {
  console.log("Body params___", body);
  const { email, password } = body;
  const response = fetch(`${AUTH_BASE_URL}/login`, { email, password });
  const data = (await response).json();
  return data;
};

let headers = new Headers();

headers.append("Content-Type", "application/json");
headers.append("Accept", "application/json");
headers.append("Access-Control-Allow-Origin", "http://localhost:3000");
headers.append("Access-Control-Allow-Credentials", "true");
headers.append("GET", "POST", "OPTIONS");
headers.append(
  "Authorization",
  "Basic " + base64.encode(username + ":" + password)
);

export const userSignup = async (body) => {
  console.log("Body params___", body);
  const { name, email, password } = body;
  const response = fetch(`${AUTH_BASE_URL}/createuser`, {
    name,
    email,
    password,
  },headers);
  const data = (await response).json();
  return data;
};
