import React,{useState} from 'react'
import './style.css'
import {loginUser} from '../../services/Api'
function Login() {
  const cred = {
    email:"",
    password:""
  }
  const [user,setUser]= useState(cred);
  const login = async (e,user)=>{
    e.preventDefault();
    const result = await loginUser(user);
    console.log('___loginResult',result);
    return result;
  }
 
  const handleChange =(e)=>{
    e.preventDefault()
    console.log('___event',e.target.value);
    // setUser(e.target.name:e.target.value)
    console.log('____userData',user)
  }

  return (
    <div className='content-holder'>
      <div className='page-header'>Login</div>
      <hr className='break-line'/>
      <form className='input-form' onSubmit={async(e)=> await login(e,user)}>
        <input className='text-input'name="email" value={user.email}  type='text'onChange={(e)=>handleChange(e)} placeholder='user@email.com'></input>
        <input className='text-input' name="password" value={user.password} type='password' onChange={(e)=>handleChange(e)} placeholder='password'></input>
        <button className='submit-btn' type='submit'>LOGIN</button>
      </form>
    </div>
  )
}

export default Login
